import './App.css';
import 'bootstrap/dist/css/bootstrap.css';
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const App = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [token, setToken] = useState(null);
  const [error, setError] = useState('');
  const [showRegisterForm, setShowRegisterForm] = useState(false); // Estado para mostrar el formulario de registro
  const navigate = useNavigate();

  // Manejar el inicio de sesión
  const handleLogin = async (e) => {
    e.preventDefault();
    setError('');

    const response = await fetch('http://localhost:3000/api/login', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ username, password }),
    });

    const data = await response.json();

    if (response.ok) {
      setToken(data.token);
      localStorage.setItem('token', data.token);
    } else {
      setError(data.error);
    }
  };

  // Manejar el registro
  const handleRegister = async (e) => {
    e.preventDefault();
    setError('');

    const response = await fetch('http://localhost:3000/api/register', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ username, password }),
    });

    const data = await response.json();

    if (response.ok) {
      alert('Usuario registrado exitosamente');
      setShowRegisterForm(false); // Ocultar formulario después del registro
    } else {
      setError(data.error);
    }
  };

  // Cerrar sesión
  const handleLogout = () => {
    setToken(null);
    localStorage.removeItem('token');
  };

  return (
    <div className="container mt-5">
      <h1 className="text-center">Login con React y Node.js</h1>

      {showRegisterForm ? (
        <form onSubmit={handleRegister}>
          <div className="mb-3">
            <label className="form-label">Nuevo Usuario</label>
            <input
              type="text"
              className="form-control"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              required
            />
          </div>

          <div className="mb-3">
            <label className="form-label">Nueva Contraseña</label>
            <input
              type="password"
              className="form-control"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </div>

          {error && <div className="alert alert-danger">{error}</div>}

          <button type="submit" className="btn btn-success">
            Registrar Usuario
          </button>
          <button
            type="button"
            className="btn btn-secondary ml-2"
            onClick={() => setShowRegisterForm(false)}
          >
            Cancelar
          </button>
        </form>
      ) : (
        <div>
          {!token ? (
            <form onSubmit={handleLogin}>
              <div className="mb-3">
                <label className="form-label">Usuario</label>
                <input
                  type="text"
                  className="form-control"
                  value={username}
                  onChange={(e) => setUsername(e.target.value)}
                  required
                />
              </div>

              <div className="mb-3">
                <label className="form-label">Contraseña</label>
                <input
                  type="password"
                  className="form-control"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  required
                />
              </div>

              {error && <div className="alert alert-danger">{error}</div>}

              <button type="submit" className="btn btn-primary">
                Iniciar sesión
              </button>
              <button
                type="button"
                className="btn btn-link"
                onClick={() => setShowRegisterForm(true)}
              >
                Registrar nuevo usuario
              </button>
            </form>
          ) : (
            <div>
              <h2 className="text-success">Sesión iniciada</h2>
              <button className="btn btn-danger" onClick={handleLogout}>
                Cerrar sesión
              </button>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default App;
