// src/Crud.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const Crud = () => {
    const [projects, setProjects] = useState([]);
    const [title, setTitle] = useState('');
    const [description, setDescription] = useState('');
    const [isEditing, setIsEditing] = useState(false);
    const [currentId, setCurrentId] = useState(null);
    const navigate = useNavigate(); // Hook para navegar a otras rutas

    // Fetch projects from the server
    const fetchProjects = async () => {
        try {
            const response = await axios.get('http://localhost:3000/api/proyectos');
            setProjects(response.data);
        } catch (error) {
            console.error('Error fetching projects:', error);
        }
    };

    useEffect(() => {
        fetchProjects();
    }, []);

    // Handle form submission for adding or updating a project
    const handleSubmit = async (e) => {
        e.preventDefault();
        if (isEditing) {
            // Update an existing project
            await axios.put(`http://localhost:3000/api/proyectos/${currentId}`, { title, description });
            setIsEditing(false);
        } else {
            // Add a new project
            await axios.post('http://localhost:3000/api/proyectos', { title, description });
        }
        setTitle('');
        setDescription('');
        fetchProjects();
    };

    const handleEdit = (project) => {
        setIsEditing(true);
        setCurrentId(project.id);
        setTitle(project.titulo);
        setDescription(project.descripcion);
    };

    const handleDelete = async (id) => {
        await axios.delete(`http://localhost:3000/api/proyectos/${id}`);
        fetchProjects();
    };

    // Navegar al formulario de pago
    const goToProtectedRoute = () => {
        navigate('/CheckoutForm');
    };

    return (
        <div>
            <h2>Project CRUD</h2>
            <form onSubmit={handleSubmit}>
                <input
                    type="text"
                    placeholder="Title"
                    value={title}
                    onChange={(e) => setTitle(e.target.value)}
                    required
                />
                <textarea
                    placeholder="Description"
                    value={description}
                    onChange={(e) => setDescription(e.target.value)}
                ></textarea>
                <button type="submit">{isEditing ? 'Update' : 'Add'}</button>
            </form>
            <ul>
                {projects.map((project) => (
                    <li key={project.id} className="list-group-item d-flex justify-content-between align-items-center">
                        <div>
                            <h3>{project.titulo}</h3>
                            <p>{project.descripcion}</p>
                        </div>
                        <div>
                            <button onClick={() => handleEdit(project)} className="btn btn-warning btn-sm">Editar</button>
                            <button onClick={() => handleDelete(project.id)} className="btn btn-danger btn-sm">Eliminar</button>
                            {/* Bot√≥n de acceso a la ruta protegida para pago */}
                            <button onClick={goToProtectedRoute} className="btn btn-info btn-sm">
                                Acceder a ruta protegida
                            </button>
                        </div>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default Crud;
