// CheckoutForm.js
import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { CardElement, useStripe, useElements } from '@stripe/react-stripe-js';
import './CheckoutForm.css';

const CheckoutForm = () => {
  const { id } = useParams(); // Obtén el ID del proyecto desde la URL
  const stripe = useStripe();
  const elements = useElements();
  const [amount, setAmount] = useState(null);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(null);
  const [loading, setLoading] = useState(true); // Nuevo estado de carga

  useEffect(() => {
    // Llama a la API para obtener el costo del proyecto
    const fetchProjectCost = async () => {
      try {
        const response = await fetch(`http://localhost:3000/api/proyectos/${id}`);
        if (!response.ok) {
          throw new Error('No se pudo obtener el costo del proyecto.');
        }
        const data = await response.json();
        setAmount(data.costo_proyecto); // Asigna el costo del proyecto al monto
        setLoading(false); // Finaliza la carga
      } catch (err) {
        setError('Error al obtener el costo del proyecto.');
        setLoading(false);
      }
    };

    fetchProjectCost();
  }, [id]);

  const handleSubmit = async (event) => {
    event.preventDefault();
    setError(null);
    setSuccess(null);

    const cardElement = elements.getElement(CardElement);

    // Llamada a tu backend para crear el PaymentIntent
    const response = await fetch('http://localhost:3000/create-payment-intent', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ amount: parseInt(amount) * 100 }), // Monto en centavos
    });

    const { clientSecret } = await response.json();

    const { error } = await stripe.confirmCardPayment(clientSecret, {
      payment_method: {
        card: cardElement,
      },
    });

    if (error) {
      setError(error.message);
    } else {
      setSuccess('Pago exitoso');
    }
  };

  if (loading) return <div>Cargando el costo del proyecto...</div>; // Muestra mensaje de carga

  if (error) return <div className="error-message">{error}</div>; // Muestra el mensaje de error si ocurre algún problema

  return (
    <form onSubmit={handleSubmit} className="checkout-form">
      <div className="form-group">
        <label htmlFor="amount">Monto a Pagar:</label>
        <input
          type="number"
          id="amount"
          name="amount"
          value={amount || ''} // Muestra el monto si está disponible
          readOnly // Campo de solo lectura
          className="amount-input"
        />
      </div>
      <CardElement className="card-element" />
      <button type="submit" disabled={!stripe} className="submit-button">
        Pagar
      </button>
      {error && <div className="error-message">{error}</div>}
      {success && <div className="success-message">{success}</div>}
    </form>
  );
};

export default CheckoutForm;
